const axios = require('axios');
const log = require('npmlog');
const checkApikey = (apikey) => {
	return new Promise((resolve, reject) => {
   	 axios.post('https://shoti-api.libyzxy0.repl.co/api/check-key', { apikey })
        .then(response => {
        	resolve(response.data.verified);
        })
        .catch(err => {
        	log.error("Error", "Error wh8le checking apikey")
        }) 
	})
}
module.exports = { checkApikey };